DROP SCHEMA IF EXISTS `stepupdb`;
CREATE SCHEMA `stepupdb` ;
USE `stepupdb`;

---DROP all tables in descending order-------
DROP TABLE IF EXISTS `stepupdb`.`order_delivery`;
DROP TABLE IF EXISTS `stepupdb`.`order`;
DROP TABLE IF EXISTS `stepupdb`.`product`;
DROP TABLE IF EXISTS `stepupdb`.`shop`;
DROP TABLE IF EXISTS `stepupdb`.`users`;

CREATE TABLE IF NOT EXISTS `stepupdb`.`users` (
  `id` INT NOT NULL,
  `first_name` VARCHAR(45) NULL,
  `last_name` VARCHAR(45) NULL,
  `mobile_no` BIGINT(10) NULL,
  `address` VARCHAR(100) NULL,
  `user_type` TINYINT(1) NULL,
  `created_at` DATETIME NULL DEFAULT NOW(),
  `updated_at` DATETIME NULL ON UPDATE NOW(),
   CONSTRAINT pk_users_id PRIMARY KEY (`id`)
 );





DROP TABLE IF EXISTS `stepupdb`.`shop`;
CREATE TABLE IF NOT EXISTS `stepupdb`.`shop` (
  `id` INT NOT NULL,
  `owner_id` INT NOT NULL,
  `name` VARCHAR(100) NULL,
  `address` VARCHAR(100) NULL,
  `contact_no` BIGINT(10) NULL,
  `licence_no` VARCHAR(100) NULL,
  `created_at` DATETIME NULL DEFAULT NOW(),
  `updated_at` DATETIME NULL ON UPDATE NOW(),
   CONSTRAINT pk_shop_id PRIMARY KEY (`id`),
   CONSTRAINT fk_owner_id FOREIGN KEY (`owner_id`) REFERENCES `stepupdb`.`users`(`id`)
 );


---------------------------------------------
DROP TABLE IF EXISTS `stepupdb`.`product`;
CREATE TABLE IF NOT EXISTS `stepupdb`.`product` (
  `id` INT NOT NULL,
  `shop_id` INT NOT NULL,
  `product_name` VARCHAR(100) NULL,
  `product_desc` VARCHAR(100) NULL,
  `product_image` TEXT NULL,
  `price` DOUBLE NULL DEFAULT 0,
  `created_at` DATETIME NULL DEFAULT NOW(),
  `updated_at` DATETIME NULL ON UPDATE NOW(),
   CONSTRAINT pk_product_id PRIMARY KEY (`id`),
   CONSTRAINT fk_shop_id FOREIGN KEY (`shop_id`) REFERENCES `stepupdb`.`shop`(`id`)
 );

SELECT * FROM `stepupdb`.`product`;



DROP TABLE IF EXISTS `stepupdb`.`order_delivery`;
DROP TABLE IF EXISTS `stepupdb`.`order`;
CREATE TABLE IF NOT EXISTS `stepupdb`.`order` (
  `id` INT NOT NULL,
  `customer_id` INT NOT NULL,
  `product_id` INT NOT NULL,
  `qty` INT NULL DEFAULT 0,
  `price` DOUBLE NULL DEFAULT 0,
  `tax` DOUBLE NULL DEFAULT 0,
  `final_amount` DOUBLE NULL DEFAULT 0,
  `created_at` DATETIME NULL DEFAULT NOW(),
  `updated_at` DATETIME NULL ON UPDATE NOW(),
   CONSTRAINT pk_id PRIMARY KEY (`id`),
   CONSTRAINT pk_customer_id FOREIGN KEY (`customer_id`) REFERENCES `stepupdb`.`users`(`id`),
   CONSTRAINT pk_product_id FOREIGN KEY (`product_id`) REFERENCES `stepupdb`.`product`(`id`)
 );




SELECT * FROM `stepupdb`.`order`;


DROP TABLE IF EXISTS `stepupdb`.`order_delivery`;
CREATE TABLE IF NOT EXISTS `stepupdb`.`order_delivery` (
  `id` INT NOT NULL,
  `order_id` INT NOT NULL,
  `delivery_person_id` INT NOT NULL,
  `delivery_status` VARCHAR(50) DEFAULT 'PENDING',
  `remark` VARCHAR(50) NULL,
  `created_at` DATETIME NULL DEFAULT NOW(),
  `updated_at` DATETIME NULL ON UPDATE NOW(),
   CONSTRAINT pk_id PRIMARY KEY (`id`),
   CONSTRAINT fk_order_id FOREIGN KEY (`order_id`) REFERENCES `stepupdb`.`order`(`id`),
   CONSTRAINT fk_delivery_person_id FOREIGN KEY (`delivery_person_id`) REFERENCES `stepupdb`.`users`(`id`)
 );



